mapper.delete:
DELETE
FROM article_tags
WHERE article_id = #{articleId}
  AND tag_id = #{tagId};

mapper.select:
SELECT COUNT(*)
FROM article_tags at
INNER JOIN article a ON at.article_id = a.article_id
WHERE tag_id IN

    #{tagId}

GROUP BY a.article_id;

SELECT a.article_id,a.title,a.click,a.comment,a.create_time, a.summary,a.user_id FROM article_tags at INNER JOIN article a ON at.article_id = a.article_id WHERE tag_id IN
#{tagId}
LIMIT #{begin},#{pageSize};
